        $x->remove($old);
    
    $val = undef;

    # Remove associations with other ends.

        
    $old->remove_source($self) if $old;
    $val->add_source($self)    if $val;

  ;

  }
  
  $self;
}


=head2 C<clear_outgoing>

  $obj->clear_outgoing;

Clears the AssociationEnd C<outgoing> links to L<UMMF::UML_1_5::Behavioral_Elements::State_Machines::Transition|UMMF::UML_1_5::Behavioral_Elements::State_Machines::Transition>.
Returns C<$obj>.

=cut
sub clear_outgoing ($) 
{
  my ($self) = @_;
  
    my $x = $self->{'outgoing'} ||= Set::Object->new();
  
  my $val; # Place holder for other MACRO.
  
    $self->{'outgoing'} = Set::Object->new(); # Recursion lock
  for my $old ( $x->members() ) {     # Recursion lock
  
    # Remove associations with other ends.

        
    $old->remove_source($self) if $old;
    $val->add_source($self)    if $val;

  ;

  }
  
  $self;
}


